    private void bt_hasilActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_hasilActionPerformed
        if(operasi != "" && operan1 != ""){
            System.out.println("Operan 1 " + operan1);
            System.out.println("Operan 2 " + operan2);
            System.out.println("Operasi " + operasi);
            operan2 = txt_c.getText();
            int a = Integer.parseInt(operan1);
            int b = Integer.parseInt(operan2);
            txt_c.setText("");
            operan1 = "";
            operan2 = "";
            switch(operasi){
                case "tambah":
                    txt_c.setText(Integer.toString(a + b));
                    break;
                case "kurang":
                    txt_c.setText(Integer.toString(a - b));
                    break;
                case "bagi":
                    txt_c.setText(Integer.toString(a / b));
                    break;
                case "kali":
                    txt_c.setText(Integer.toString(a * b));
                    break;
            }
        }
    }//GEN-LAST:event_bt_hasilActionPerformed
    
        String operasi = "";
        String operan1 = "";
        String operan2 = "";
        String hasil = "";
    public void inputData(String data){
        //check String in txt_c
        if(txt_c.getText() != ""){
            String a = txt_c.getText();
            txt_c.setText(a + data);
        } else{
            txt_c.setText(data);
        }
    }
    
    public void CekOperasi(String data){
        operasi = data;
        operan1 = txt_c.getText();
        txt_c.setText("");
    }
